import { useEffect, useState } from "react";
import Avatar from "./Avatar";

export default function Chat() {
    const [ws, setWs] = useState(null);
    const [onlinePeople, setOnlinePeople] = useState({});
    const [selectUserId,setSelectUserId] = useState(null);
    useEffect(() => {
        const ws = new WebSocket('ws://localhost:4000');
        setWs(ws);
        ws.addEventListener('message', handleMessage);
    }, []);
    function showOnlinePeople(peopleArray) {
        const people = {};
        peopleArray.forEach(({ userId, username }) => {
            people[userId] = username;
        });
        // console.log(people);
        setOnlinePeople(people);
    }
    function handleMessage(ev) {
        const messageData = JSON.parse(ev.data);
        console.log(messageData);
        // console.log('new message',e);
        if ('online' in messageData) {
            showOnlinePeople(messageData.online);
        }
    }
    
    return (
        <div className="flex h-screen">
            <div className="bg-white-100 w-1/4">
                
                {Object.keys(onlinePeople).map(userId => (
                    <div onClick={()=>setSelectUserId(userId)} 
                        className={"border-b border-gray-100 py-2 pl-4 flex items-center gap-2 cursor-pointer "+(userId === selectUserId ? 'bg-green-50':'')}>
                        <Avatar username={onlinePeople[userId]} userId={userId}/>
                        <span className="text-gray-800">{onlinePeople[userId]}</span>
                    </div>
                ))}
            </div>
            <div className="flex flex-col bg-green-100 w-3/4 p-2">
                <div className="flex-grow" >message the selected guy</div>
                <div className="flex gap-2">
                    <input type="text" placeholder="Type message here..." className="bg-white flex-grow border p-2 rounded-sm" />
                    <button className="bg-green-500 p-2 text-white rounded-sm">
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6">
                            <path stroke-linecap="round" stroke-linejoin="round" d="M6 12 3.269 3.125A59.769 59.769 0 0 1 21.485 12 59.768 59.768 0 0 1 3.27 20.875L5.999 12Zm0 0h7.5" />
                        </svg>

                    </button>
                </div>
            </div>
        </div>
    );
}
