import { useContext, useEffect, useRef, useState } from "react";
import Avatar from "./Avatar";
import Logo from "./Logo";
import { UserContext } from "./UserContext";
import { uniqBy } from "lodash";
import axios from "axios";

export default function Chat() {
    const [ws, setWs] = useState(null);
    const [onlinePeople, setOnlinePeople] = useState({});
    const [selectUserId, setSelectUserId] = useState(null);
    const { username, id } = useContext(UserContext);
    const [newMessagetxt, setNewMessagetxt] = useState("");
    const [messages, setMessages] = useState([]);
    const messageDivRef = useRef(); 
    useEffect(() => {
        connectToWs();
    }, []);
    function connectToWs(){
        const ws = new WebSocket('ws://localhost:4000');
        setWs(ws);
        ws.addEventListener('message', handleMessage);
        ws.addEventListener('close',()=>{
            setTimeout(()=>{
                console.log("Disconnected, Trying to reconnect!!");
                connectToWs();
            },1000);
        });
    }
    function showOnlinePeople(peopleArray) {
        const people = {};
        peopleArray.forEach(({ userId, username }) => {
            people[userId] = username;
        });
        // console.log(people);
        setOnlinePeople(people);
    }
    function handleMessage(ev) {
        const messageData = JSON.parse(ev.data);
        console.log({ ev, messageData });
        // console.log('new message',e);
        if ('online' in messageData) {
            showOnlinePeople(messageData.online);
        }
        else if ('text' in messageData) {
            setMessages(prev => ([...prev, { ...messageData }]));
        }
    }
    function sendMessage(ev) {
        ev.preventDefault();
        ws.send(JSON.stringify({
            recepient: selectUserId,
            text: newMessagetxt,
        }));
        setNewMessagetxt('');
        setMessages(prev => ([...prev, {
            text: newMessagetxt,
            sender: id,
            recepient: selectUserId,
            id: Date.now(),
        }]));
    }

    useEffect(()=>{
        const div = messageDivRef.current;
        if(div){
            div.scrollIntoView({behavior:'smooth',block:'end'});
        }
    },[messages])

    useEffect(()=>{
        if(selectUserId){
            axios.get('/messages/'+selectUserId);
        }
    },[selectUserId])

    const UsersExcludingOwn = { ...onlinePeople };
    delete UsersExcludingOwn[id];

    const messagesOnce = uniqBy(messages, 'id')

    return (
        <div className="flex h-screen">
            <div className="bg-white-100 w-1/4">
                <Logo />
                {Object.keys(UsersExcludingOwn).map(userId => (
                    <div key={userId} onClick={() => setSelectUserId(userId)}
                        className={"border-b border-gray-100 flex items-center gap-2 cursor-pointer " + (userId === selectUserId ? 'bg-green-50' : '')}>
                        {userId === selectUserId && (
                            <div className="w-1 bg-green-500 h-12 rounded-r-md"></div>
                        )}
                        <div className="flex gap-2 py-2 pl-4 items-center">
                            <Avatar username={onlinePeople[userId]} userId={userId} />
                            <span className="text-gray-800">{onlinePeople[userId]}</span>
                        </div>

                    </div>
                ))}
            </div>
            <div className="flex flex-col bg-green-100 w-3/4 p-2">
                <div className="flex-grow" >
                    {!selectUserId && (
                        <div className="flex h-full flex-grow items-center justify-center">
                            <div className="text-lg text-gray-300">&larr; Select your friend and Chat... 👨‍💻</div>
                        </div>
                    )}
                    {!!selectUserId && (
                        <div className="relative h-full">
                            <div className="overflow-y-scroll absolute inset-0">
                                {messagesOnce.map(message => (
                                    <div className={(message.sender === id ? 'text-right' : 'text-left')}>
                                        <div className={"text-left inline-block p-2 my-2 text-sm rounded-md " + (message.sender === id ? 'bg-blue-500 text-white' : 'bg-white text-gray-500')}>
                                            {message.text}
                                        </div>
                                    </div>
                                ))}
                                <div ref={messageDivRef}></div>
                            </div>
                        </div>


                    )}
                </div>
                {!!selectUserId && (
                    <form className="flex gap-2" onSubmit={sendMessage}>
                        <input type="text" value={newMessagetxt} onChange={ev => setNewMessagetxt(ev.target.value)} placeholder="Type message here..." className="bg-white flex-grow border p-2 rounded-sm" />
                        <button type="submit" className="bg-green-500 p-2 text-white rounded-sm">
                            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6">
                                <path stroke-linecap="round" stroke-linejoin="round" d="M6 12 3.269 3.125A59.769 59.769 0 0 1 21.485 12 59.768 59.768 0 0 1 3.27 20.875L5.999 12Zm0 0h7.5" />
                            </svg>

                        </button>
                    </form>
                )}

            </div>
        </div>
    );
}
